import React,{useEffect,useState} from 'react'
import axios from 'axios'
import { useParams } from 'react-router'
import { useNavigate } from 'react-router'
import Nav from '../Nav/Nav'

function UpdateUser() {

    const [inputs, setInputs] = useState({});
    const history = useNavigate();
    const id  = useParams().id;

    useEffect(()=>{
        const fetchHandler = async()=>{
        await axios
        .get(`http://localhost:5000/users/${id}`)
        .then((res)=> res.data)
        .then((data)=>setInputs(data.user));
        };
        fetchHandler();
    },[id]);

    const sendRequest = async () => {
        await axios
        .put(`http://localhost:5000/users/${id}`,{
            name: String(inputs.name),
            gmail: String(inputs.gmail),
            age: String(inputs.age),
            address: String(inputs.address),
        })
        .then((res)=>res.data);
    };

    const handleChange = (e) => { // Destructure `name` and `value` from the event target
        setInputs((prevState) => ({
            ...prevState,
            [e.traget.name]: e.traget.name, // Update the corresponding input field
        }));
    };

    const handleSubmit = async(e) => {
        e.preventDefault();
        console.log(inputs);
        sendRequest().then(() =>
        history('/userdetails'));
    };

  return (
    <div>
      <h1>Update User</h1>
      <form onSubmit={handleSubmit}>
                <label>Name:</label>
                <br />
                <input
                    type="text"
                    name="name"
                    onChange={handleChange}
                    value={inputs.name}
                    required
                />
                <br />
                <br />
                <label>Email:</label>
                <br />
                <input
                    type="email"
                    name="gmail"
                    onChange={handleChange}
                    value={inputs.gmail}
                    required
                />
                <br />
                <br />
                <label>Age:</label>
                <br />
                <input
                    type="number"
                    name="age"
                    onChange={handleChange}
                    value={inputs.age}
                    required
                />
                <br />
                <br />
                <label>Address:</label>
                <br />
                <input
                    type="text"
                    name="address"
                    onChange={handleChange}
                    value={inputs.address}
                    required
                />
                <br />
                <br />
                <button type="submit">Submit</button>
            </form>
    </div>
  )
}

export default UpdateUser;
